// Contact Form
        function validateForm() {
            var name = document.forms["myForm"]["name"].value;
            var email = document.forms["myForm"]["email"].value;
            var comments = document.forms["myForm"]["comments"].value;
            document.getElementById("error-msg").style.opacity = 0;
            document.getElementById('error-msg').innerHTML = "";
            if (name == "" || name == null) {
                document.getElementById('error-msg').innerHTML = "<div class='alert alert-warning'>*Please enter a Name*</div>";
                fadeIn();
                return false;
            }
            if (email == "" || email == null) {
                document.getElementById('error-msg').innerHTML = "<div class='alert alert-warning'>*Please enter a Email*</div>";
                fadeIn();
                return false;
            }
            if (comments == "" || comments == null) {
                document.getElementById('error-msg').innerHTML = "<div class='alert alert-warning'>*Please enter a Comments*</div>";
                fadeIn();
                return false;
            }

            var xhttp = new XMLHttpRequest();
            xhttp.onreadystatechange = function () {
                if (this.readyState == 4 && this.status == 200) {
                    document.getElementById("simple-msg").innerHTML = this.responseText;
                    document.forms["myForm"]["name"].value = "";
                    document.forms["myForm"]["email"].value = "";
                    document.forms["myForm"]["comments"].value = "";
                }
            };
            xhttp.open("POST", "php/contact.php", true);
            xhttp.setRequestHeader("Content-type", "application/x-www-form-urlencoded");
            xhttp.send("name=" + name + "&email=" + email + "&comments=" + comments);
            return false;
        }

        function fadeIn() {
            var fade = document.getElementById("error-msg");
            var opacity = 0;
            var intervalID = setInterval(function () {
                if (opacity < 1) {
                    opacity = opacity + 0.5;
                    fade.style.opacity = opacity;
                } else {
                    clearInterval(intervalID);
                }
            }, 200);
        }
    // Contact Us End

    // Research
    document.addEventListener('DOMContentLoaded', function () {
            const container = document.querySelector('.blog-container');
            const scrollLeftBtn = document.getElementById('scroll-left');
            const scrollRightBtn = document.getElementById('scroll-right');
            let autoScrollInterval;

            const scrollAmount = 300; // Random value based on the width of your blog items 350

            function scrollLeft() {
                container.scrollBy({ left: -scrollAmount, behavior: 'smooth' });
            }

            function scrollRight() {
                container.scrollBy({ left: scrollAmount, behavior: 'smooth' });
            }

            function startAutoScroll() {
                autoScrollInterval = setInterval(() => {
                    scrollRight();
                }, 4000);
            }

            function stopAutoScroll() {
                clearInterval(autoScrollInterval);
            }

            scrollLeftBtn.addEventListener('click', () => {
                stopAutoScroll();
                scrollLeft();
                startAutoScroll();
            });

            scrollRightBtn.addEventListener('click', () => {
                stopAutoScroll();
                scrollRight();
                startAutoScroll();
            });

            container.addEventListener('mouseover', stopAutoScroll);
            container.addEventListener('mouseout', startAutoScroll);

            // Start automatic scrolling initially
            startAutoScroll();
        });